import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useRecoilState } from 'recoil';
import moviesState from '../atoms/moviesAtom';
//import DeleteMessages from './DeleteMessages';
import relativeTime from 'dayjs/plugin/relativeTime'
import dayjs from 'dayjs'
//import EditMessage from './EditMessage';


const ListMovies = () => {
    const [movies,setMovies]=useRecoilState(moviesState)
    dayjs.extend(relativeTime)


    const getData = async()=>{
        const res =await axios.get(`${process.env.REACT_APP_API_URL}/movies`,{
            withCredentials:true
        })
        var adddate=new Date('2019-3-5')
        var seentoday=[
            {title: "abc",
            added:adddate,
            external_id:"123"}]
        //res.data
        setMovies(seentoday)
    }

    useEffect(()=>{
        getData()   
    },[])
  
    return <div className='pt-40 w-50 mx-auto'>
        {movies.map(movie=> <div key={movie._id} className='card mb-10 relative'>
            

                <div className='card-body'>
                    <p>{movie.title}</p>
                    <p className='txt-small txt-right'>-- {movie.added.toString}</p>
                    </div>
                
            </div>)}
    </div>;
};

export default ListMovies;

