import React, { useState } from 'react'

const TestForm=()=>{
    const [form, setForm]=useState({
        input:'',
        f2:''

    })
    //const [f2, setInputValue2]=useState('')

    const handlerOnChange=(event)=>{
        const {value,name}=event.target
        console.log(name,value)
        setForm({...form,[name]: value})
    }
    
    return (
        <form>
            <label htmlFor='test'>enter?</label>
            <input onChange={handlerOnChange} id ='test' type="text" value ={form.input} name="inputTest" placeholder='Enter sth'/>
            <label htmlFor='field2'>Second</label>
            <input onChange={handlerOnChange} id ='f2' type="text" value ={form.f2} name="input" placeholder='Enter sth'/>

        </form>
    )
}

export default TestForm
