import React, { useState } from 'react'

const ListAnimals=()=>{
    const [newAnimal,setnew]=useState('')
    const [Animals,setAnimal]=useState(['rabbit','elephant','lion'])

    const handlerChange=(event)=>{
        setnew(event.target.value)
    }
    const handlerSetNew=(event)=>{
        event.preventDefault()
        setAnimal([...Animals,newAnimal])
        setnew('')
    }

    return(
        <div>
        <form onSubmit={handlerSetNew}>
            <input 
                onChange={handlerChange} 
                type="text" 
                value ={newAnimal} 
                name="inputanimal" 
                placeholder='Enter animal'
                />
            <button type='submit'>Add Animal</button>

        </form>
        <ul>
            {Animals.map((animal,index)=> <li key={'Animals-'+index}>{animal}</li>)}
        </ul>
        </div>
    )
}
export default ListAnimals
