import axios from 'axios';
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useRecoilState } from 'recoil';
import moviesState from '../atoms/moviesAtom'

const FormMovies = () => {
    let navigate=useNavigate()
    const[movies,setMovies]=useRecoilState(moviesState)
    const[form,setForm]=useState({content:'',picture:''})

    const handlerSubmit=async(event)=>{
        event.preventDefault()

        console.log(form)

        var formData=new FormData()
        formData.append('content',form.content)
        formData.append('picture',form.picture)

        

        const res=await axios.post(`${process.env.REACT_APP_API_URL}/movies`,formData,{
            withCredentials:true,
            headers:{
                'Content-Type': 'multipart/form-data'
            }
        })

        /*const res=await axios.post(`${process.env.REACT_APP_API_URL}/messages`,form,{
            withCredentials:true
        })
        console.log(res.data)
        */
        setMovies([...movies,res.data])
        setForm({content:'',picture:null})
    }

    const handlerOnChange=async(event)=>{
        const {name,value} = event.target
        setForm({...form,[name]:value})
    }

    const handlerOnChangeImage=async(event)=>{
        const target=event.target
        const files=target.files

        if(files){
            const file=files[0]
            setForm({...form,picture:file})
        }
    }

  return <form className='w-50 mx-auto form-bordered'>
      <div className='form-group'>
            <textarea
                name="content"
                value={form.content}
                placeholder='yyyy-mm-dd'
                className='form-textarea'
                onChange={handlerOnChange}
            ></textarea>
        </div>
        <div className='form-group'>
            <button className='btn bg-red-400 txt-white w-100'>
                Get Movies added in the day
            </button>
        </div>

  </form>
  
};
//onSubmit={handlerSubmit} 
/**<div className='form-group'>
            <input type="file" name='picture' onChange={handlerOnChangeImage}/>
        </div> */
/**<div className='form-group'>
            <button className='btn bg-red-400 txt-white w-100'>
                create the message
            </button>
        </div> */

export default FormMovies;
