import axios from 'axios';
import React, { useState } from 'react';
import { FaTrashAlt } from 'react-icons/fa';
import { useNavigate } from 'react-router-dom';
import { useRecoilState } from 'recoil';
//import { post } from '../../../api/routes/messages';
import messagesState from '../atoms/messagesAtom';

const EditMessage = ({message}) => {
    let navigate=useNavigate()
    const[messages,setMessages]=useRecoilState(messagesState)
    const[form,setForm]=useState({content:'',picture:''})
    
    
    //const [messages,setMessages]=useRecoilState(messagesState)
    const handlerOnClick=async(event)=>{

        event.preventDefault()

        console.log(form)

        var formData=new FormData()
        formData.append('content',form.content)
        formData.append('picture',form.picture)

        try {
            const res=await axios.put(`${process.env.REACT_APP_API_URL}/messages/${message._id}`,formData,{
                withCredentials:true,
                headers:{
                    'Content-Type': 'multipart/form-data'
                }})
                setMessages[indexOf(setMessages)]=res.data
                setForm({content:'',picture:null})        
        } catch (error) {
            console.log(error)
        }
        
        
    }

    const handlerOnChange=async(event)=>{
        const {name,value} = event.target
        setForm({...form,[name]:value})
    }
    const handlerOnChangeImage=async(event)=>{
        const target=event.target
        const files=target.files

        if(files){
            const file=files[0]
            setForm({...form,picture:file})
        }
    }

    return <div
            className='edit'>
            <textarea
                name="content"
                value={form.content}
                placeholder='Add your message here'
                className='form-textarea'
                onChange={handlerOnChange}
            ></textarea>
            <div className='form-group'>
            <input type="file" name='picture' onChange={handlerOnChangeImage}/>
        </div>
            <div className='form-group'>
                <button className='btn bg-red-400 txt-white w-100' onClick={handlerOnClick}>
                    edit the message
                </button>
            </div>
            
        </div>;
};

export default EditMessage;
