import React, { useState } from 'react'
import RegisterForm from '../components/RegisterForm'
const Register = () => {
    const [newName,setNewName]=useState('')
    const [newPW,setNewPW]=useState('')
    const [Regiinfo,setRegiInfo]=useState(['user','123456'])

    const handlerNameChange=(event)=>{
        setNewName(event.target.value)
    }
    const handlerPWChange=(event)=>{
        setNewPW(event.target.value)
    }
    const handlerSetNew=(event)=>{
        event.preventDefault()
        setRegiInfo([newName,newPW])
        setRegiInfo('')
    }
    return (
        
        <div>
           <form onSubmit={handlerSetNew}>
            <input onChange={handlerNameChange} type="text" value ={newName} name="username" placeholder='Enter name'/>
            <input onChange={handlerPWChange} type="text" value ={newPW} name="password" placeholder='Enter password'/>
            <input type="submit" value="register"/>
            
        </form>
        </div>
    )
}

export default Register
