import React,{useContext, useEffect} from 'react'
import {BrowserRouter,Link,Route, Routes, useRoutes} from 'react-router-dom'


import Hello from './components/Hello'
import Facts from './components/Increment'
import SwicherMode from './components/SwicherMode'
import { ThemeContext } from './context/themes'
import Testform from './components/TestForm'
//import Animals from './components/ListAnimals'
import {useRecoilState} from 'recoil'

import Home from './pages/Home'
import Animals from './pages/Animals'
import Login from './pages/Login'
import Register from './pages/Register'
import axios from 'axios'
import Logout from './pages/Logout'

import userState from "./atoms/userAtom"

export const App = () => {
  const [{theme}]=useContext(ThemeContext)
  const [user,setUser] = useRecoilState(userState)

  useEffect(()=>{
    const getUser =async()=>{
      try {
        const res=await axios.get(`${process.env.REACT_APP_API_URL}/me`,{
          withCredentials:true
        })
        setUser({
          isAuth:true,
          user:res.data
        })
        console.log("getUser", res.data)
      } catch (error) {
        console.error(error.response.data)
      }
    }
    getUser()
  })
  return (<BrowserRouter>
    <div className='app' style={theme}>
      <SwicherMode />
      <nav>
        <Link to="/">Home</Link>
        <Link to="/animals">Animals</Link>
        {!user.isAuth && <Link to ='/login'>Login</Link>}
        { !user.isAuth && <Link to='/register'>Register</Link> }
        { user.isAuth && <Link to ='/account'>{user.user.email}</Link>}
        { user.isAuth && <Link to ='/logout'>Logout</Link>}
      </nav>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/animals" element={<Animals />} />
        <Route path="/login" element={<Login />}/>
        <Route path="/register" element={<Register />}/>
        <Route path='/logout' element={<Logout />} />
      </Routes>
    </div>
    </BrowserRouter>
  )
}

export default App
